

public class Application {
    
    public static int compareSequences(String sequenceA, String sequenceB){
        CompareNucleoSequence compareNucleoSequence = new CompareNucleoSequence(); //metodo costruttore
        int countMatch = 0;
        for (int i = 0; i<sequenceA.length() && i< sequenceB.length(); i++){
            if (sequenceA.charAt(i) == sequenceB.charAt(i)){
                countMatch++;
            }
            if ((i+1)%ACAPO == 0){
                System.out.print(compareNucleoSequence.printIntervalSequence(
                        sequenceA, sequenceB, ACAPO,(i+1)-ACAPO));
                System.out.print("  |  ");
                System.out.print(compareNucleoSequence.printIntervalSequence(
                        sequenceB, sequenceA, ACAPO,(i+1)-ACAPO));
                System.out.println(" ");
            }
        }
        return countMatch;
    }

    public static void printResults(String sequenceA, String sequenceB){
        int countMatch = compareSequences(sequenceA, sequenceB);
        System.out.println("Matches");
        System.out.println("Numero di match " + countMatch + " su una lunghezza di " + sequenceA.length());
        
    }

    public static final String ANSI_RESET = "\u001B[0m";
    public static final String ANSI_BLACK = "\u001B[30m";
    public static final String ANSI_RED = "\u001B[31m";
    public static final int ACAPO = 50;

    public static void main(String[] args){
        CompareNucleoSequence compareNucleoSequence = new CompareNucleoSequence(); //metodo costruttore

        compareNucleoSequence.setSequenceNucleo("");
        compareNucleoSequence.setSequenceNucleo2("");
        compareNucleoSequence.setSequenceNucleo3("");
        compareNucleoSequence.setSequenceNucleo4("");



        //compara la sequenza base con la sequenza 2

        printResults(compareNucleoSequence.getSequenceNucleo(), compareNucleoSequence.getSequenceNucleo2());

        //comparo 2 con 3 

        printResults(compareNucleoSequence.getSequenceNucleo2(), compareNucleoSequence.getSequenceNucleo3());

    }
}


